<?phprequire '../class/db_class.php';$DB = new DB();$theme = $_GET['theme'];$cpf = $_GET['cpf'];if(!$theme) {	return false;}	if (isset($theme) AND $theme != 'tous' AND $cpf == 'false'){		$data_formation = $DB->query('SELECT distinct nom_formation_a from formation_atlas where theme_formation_a = :theme',		                        array('theme' => $_GET['theme'])		                        );	}	elseif ($cpf == 'true') {		$data_formation = $DB->query('SELECT distinct nom_formation_a from formation_atlas fa, formation_cpf fc 									  WHERE fa.id_formation_a  = fc.id_formation								      AND theme_formation_a = :theme AND cadre_cpf = \'oui\' ',		                        array('theme' => $_GET['theme'])		                        );	}	else{		$data_formation = $DB->query('SELECT distinct nom_formation_a from formation_atlas');	}?>	<input type="hidden" value="<?= $_GET['cpf']; ?>" id="input-cpf">	<label for="select_formation">SÃ©lectionner une formation (module)</label>	<select id="select_formation" name="select_formation" class="select2js form-control">		<option value="" selected disabled>Choisir une formation</option>		<?php		foreach($data_formation as $uneFormation) {			?>			<option value="<?php echo $uneFormation->nom_formation_a; ?>"><?php echo $uneFormation->nom_formation_a; ?></option>			<?php		}		?>	</select><script type="text/javascript">	$(document).ready(function(){		$("select#select_formation").change(function(){			var nom_formation = $("select#select_formation option:selected").val();			var cpf = $("#input-cpf").val();			$.ajax({				type:"GET",				url:"ajax/niveau.php",				data:{'nom_formation' : nom_formation,					  'cpf' : cpf},				success: function(html){					$("#div-niveau").html(html);				}			});			});	});</script>    <script type="text/javascript">      $(document).ready(function() {     	 $(".select2js").select2({minimumResultsForSearch: 5, theme: "bootstrap"});      		      		$(".select2js").on("change",function(){		   	  resetFields(2);		   });      });    </script>